package ecust.system.core.metadata;import ecust.system.core.metadata.catalog.PCBStateEnum;// 进程控制块public class PCB {	// 内部标识符	private int id;	// 外部标识符	private String name;	// 进程优先级大小	private int priority;	// 进程创建时间	private int createTime;	// 进程第一次调用时间	private int firstCallTime;	// 进程完成时间	private int finishTime;	// 进程完成所需时间	private int requiredTime;	// 进程已运行时间	private int ranTime;	// 进程状态	private PCBStateEnum state;	// 进程其他信息	private Other other;	public int getId() {		return id;	}	public void setId(int id) {		this.id = id;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public int getPriority() {		return priority;	}	public void setPriority(int priority) {		this.priority = priority;	}	public int getCreateTime() {		return createTime;	}	public void setCreateTime(int createTime) {		this.createTime = createTime;	}	public int getFirstCallTime() {		return firstCallTime;	}	public void setFirstCallTime(int firstCallTime) {		this.firstCallTime = firstCallTime;	}	public int getFinishTime() {		return finishTime;	}	public void setFinishTime(int finishTime) {		this.finishTime = finishTime;	}	public int getRequiredTime() {		return requiredTime;	}	public void setRequiredTime(int requiredTime) {		this.requiredTime = requiredTime;	}	public int getRanTime() {		return ranTime;	}	public void setRanTime(int ranTime) {		this.ranTime = ranTime;	}	public PCBStateEnum getState() {		return state;	}	public void setState(PCBStateEnum state) {		this.state = state;	}	public Other getOther() {		return other;	}	public void setOther(Other other) {		this.other = other;	}	@Override	public String toString() {		return "PCB{" +				"id=" + id +				", name='" + name + '\'' +				", priority=" + priority +				", createTime=" + createTime +				", firstCallTime=" + firstCallTime +				", finishTime=" + finishTime +				", requiredTime=" + requiredTime +				", ranTime=" + ranTime +				", state=" + state +				", other=" + other +				'}';	}}